const db = require('./config/database');

// Images d'exemple (base64 petites images de démonstration)
const sampleImages = [
    'data:image/svg+xml;base64,',
    'data:image/svg+xml;base64,',
    'data:image/svg+xml;base64,',
    'data:image/svg+xml;base64,',
    'data:image/svg+xml;base64,'
];

async function addSampleImages() {
    try {
        // Récupérer tous les produits sans image
        const [products] = await db.query('SELECT id FROM Produit WHERE image_url IS NULL LIMIT 100');
        
        if (products.length === 0) {
            console.log('✅ Tous les produits ont déjà une image');
            process.exit(0);
        }
        
        let updated = 0;
        for (let product of products) {
            const randomImage = sampleImages[Math.floor(Math.random() * sampleImages.length)];
            await db.query('UPDATE Produit SET image_url = ? WHERE id = ?', [randomImage, product.id]);
            updated++;
        }
        
        console.log(`✅ ${updated} produits ont été mis à jour avec des images de démonstration`);
        process.exit(0);
    } catch (error) {
        console.error('❌ Erreur:', error.message);
        process.exit(1);
    }
}
